        <div className="color-categories">
          {PRICE_CATEGORIES.map((cat) => (
            <li key={cat.name} className="category">
              <button
                className="btn btn-category"
                style={{
                  backgroundColor: cat.color,
                  border: currentPrice.includes(cat.name)
