import { useEffect, useState } from "react";
// import supabase from "./supabase";
import "./style.css";

const CATEGORIES = [
  // { name: "technology", color: "#3b82f6" },
  // { name: "science", color: "#16a34a" },
  { name: "red", color: "#ef4444" },
  // { name: "society", color: "#eab308" },
  { name: "pink", color: "#db2777" },
  // { name: "health", color: "#14b8a6" },
  { name: "orange", color: "#f97316" },
  // { name: "news", color: "#8b5cf6" },
];

const initialFacts = [
  {
    id: 1,
    text: "React is being developed by Meta (formerly facebook)",
    source: "https://opensource.fb.com/",
    category: "red",
    votesInteresting: 24,
    votesMindblowing: 9,
    votesFalse: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "Millennial dads spend 3 times as much time with their kids than their fathers spent with them. In 1982, 43% of fathers had never changed a diaper. Today, that number is down to 3%",
    source:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "pink",
    votesInteresting: 11,
    votesMindblowing: 2,
    votesFalse: 0,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Lisbon is the capital of Portugal",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "orange",
    votesInteresting: 8,
    votesMindblowing: 3,
    votesFalse: 1,
    createdIn: 2015,
  },
];

const all_colors = ["pink", "orange", "red"];
const all_benefits = [
  "Hydrating",
  "Long-wearing",
  "Plumping",
  "Transfer-resistant",
  "Transfer-proof",
  "Water-proof",
];

const all_prices = ["Under $25", "$25 to $50", "$50 to $100", "$100 and above"];

function App() {
  const [showForm, setShowForm] = useState(false);
  const [facts, setFacts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentColor, setCurrentColor] = useState(all_colors);
  const [currentBenefit, setCurrentBenefit] = useState(all_benefits);
  const [currentPrice, setCurrentPrice] = useState(all_prices);

  useEffect(
    function () {
      async function getFacts() {
        setIsLoading(true);
        // let query = supabase.from("facts").select("*");
        // if (currentCatgory !== "all")
        //   query = query.eq("category", currentCatgory);
        // const { data: facts, error } = await query
        //   .order("votesInteresting", { ascending: false })
        //   .limit(1000);

        // if (!error) setFacts(facts);
        // else alert("There was a problem getting data");
        setFacts(initialFacts);

        setIsLoading(false);
      }
      getFacts();
    },
    [currentColor]
  );

  return (
    <>
      <Header showForm={showForm} setShowForm={setShowForm} />

      {showForm ? (
        <NewFactForm setFacts={setFacts} setShowForm={setShowForm} />
      ) : null}

      <main className="main">
        <ColorFilter setCurrentCategory={setCurrentColor} />
        <BenefitFilter setCurrentBenefit={setCurrentBenefit} />
        <PriceFilter setCurrentPrice={setCurrentPrice} />
        {isLoading ? (
          <Loader />
        ) : (
          <FactList facts={facts} setFacts={setFacts} />
        )}
      </main>
    </>
  );
}

function Loader() {
  return <p className="message">Loading ...</p>;
}

function Header({ showForm, setShowForm }) {
  const appTitle = "Lipstick Expert";
  return (
    <header className="header">
      <div className="logo">
        <img
          src="lipstick_finall.png"
          height="68"
          width="68"
          alt="Lipstick Expert Logo"
        />
        <h1>{appTitle}</h1>
      </div>
      <button
        className="btn btn-large btn-open"
