                onClick={() => {
                  setCurrentColor((colors) => {
                    if (colors.includes(cat.name)) {
                      // console.log(colors);
                      // If the color is already in the list, return the current list without modification
                      // if (colors.length === all_colors.length) {
                      //   return [cat.name];
